import {useParams} from "react-router-dom"
import { useState } from "react";
import { useDispatch, useSelector } from 'react-redux/';
import {  deleteTodo } from "../../redux/modules/todo";
import {useNavigate} from "react-router-dom";
import styled from "styled-components";

import Postmodal from "../postmodal/Postmodal";

const Detail = () =>{
  let navigate = useNavigate();
  let dispatch = useDispatch();
    let [modal, setModal] = useState(false);
    let {id} = useParams();
    const  todos  = useSelector((state) => state.todos);

    let todo = todos.find((todo)=>{
      return String(todo.id) === id;
    })
    console.log(todo)


  const close=()=>{
    setModal(false);
  }

    return (
      <>{modal? <Postmodal post={todo} close={close} />:null}
      <div style={{backgroundColor:"orange"}}>게시글</div>
      <DetailContainer >
        <DetailBackButton onClick={()=>navigate(-1)} >이전으로</DetailBackButton>
          <h4>{todo.title}</h4>
          <p>{todo.body}</p>
          <p>{todo.date}</p>
          <p>{todo.writer}</p>
          <div>
            <p>{todo.count}</p>
          <button onClick={()=>{
            setModal(true);
          }}>수정하기</button>
          <button onClick={()=>{
            dispatch(deleteTodo(todo.id));
            navigate("/", {replace:true});
          }}>삭제하기</button>
          </div>
        </DetailContainer>
      </>
    )
  }

  export default Detail;


const DetailContainer = styled.div`
padding: 30px;
`
const DetailBackButton = styled.button`
display: flex;
justify-content: end;

`
const DetailId = styled.h1`
font-size: 24px;
`
const DetailBack = styled.div`
font-size: 26px;
border: 1px solid #eee;
width: 150px;
height: 40px;
text-align: center;
padding: 5px;
`
const DetailTit = styled.h1`
font-size: 26px;
margin-bottom: 20px;
`
const DetailDesc = styled.p`
font-size: 16px;`
